import React, { useState, useEffect } from 'react';

const address = [
  "0x2CB99F193549681e06C6770dDD5543812B4FaFE8 10",
  "0xEb0D38c92deB969b689acA94D962A07515CC5204 5",
  "0x8B3392483BA26D65E331dB86D4F430E9B3814E5e 15",
  "0xF4aDE8368DDd835B70b625CF7E3E1Bc5791D18C1 8d",
  "0x8B3392483BA26D65E331dB86D4F430E9B3814E5e 15",
  "0x09ae5A64465c18718a46b3aD946270BD3E5e6aaB 9",
]

const Disperse = () => {
  const [inputValue, setInputValue] = useState(address);
  const [error, setError] = useState(null);
  const [splitdata, setSplitData] = useState([]);
  const [showbutton, setShowbutton] = useState(false);
  const [keepFirstOneClicked, setKeepFirstOneClicked] = useState(false);

  useEffect(() => {
    const tempSplitData = inputValue.map((str) => {
      const [address, amount] = str.split(' ');
      return { address, amount: amount };
    });

    setSplitData(tempSplitData);
  }, [inputValue]);

  const next = () => {
    const errors = [];
    const seenAddresses = new Set();

    const tempFilteredSplitData = splitdata.filter((item, index) => {
      if (isNaN(item.amount)) {
        errors.push(`Line ${index + 1} wrong amount`);
        return true;
      }
      if (seenAddresses.has(item.address)) {

        errors.push(`Line ${index + 1} has a duplicate address`);
        setShowbutton(true)

      } else {
        seenAddresses.add(item.address);
        return true; // Keep the line with a unique address
      }
    });
    if (errors.length > 0) {
      setError(errors);
    } else {
      setError([]);
      setShowbutton(false);
    }

      setSplitData(tempFilteredSplitData);
  }


    return (
      <div
        style={{
          width: '100%',
          height: '100vh',
          backgroundColor: 'whitesmoke',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        <div
          style={{
            width: '90%',
            height: '90%',
            backgroundColor: 'white',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <div style={{ width: '50%', height: '50%', padding: '3vw' }}>
            <h4>Addresses with Amounts</h4>

            <textarea
  cols={5}
  style={{ width: '100%', height: '80%', display: 'block' }}
  value={
    keepFirstOneClicked && splitdata.length > 0
      ? splitdata.map((item) => `${item.address} ${item.amount}`).join('\n')
      : inputValue.join('\n')
  }
  onChange={(e) => setInputValue(e.target.value.split('\n'))}
></textarea>

       {showbutton && (
  <div style={{ display: 'flex', justifyContent: 'flex-end', alignItems: 'center' ,color:"red"}}>
    <button style={{ backgroundColor: 'transparent', color: 'red', border: 'none', marginRight: '5px' }}   onClick={() => {
                  setKeepFirstOneClicked(true);
                  // setError([])
                  {next()}
  }}  >
      Keep first one
    </button>
    |
    <button style={{ backgroundColor: 'transparent', color: 'red', border: 'none', marginLeft: '5px' }}>
      Combine Balance
    </button>
  </div>
)}


           {error && (
  <div>
    {error.map((errorMessage, index) => (
      <p key={index} style={{ color: 'red', border: '1px solid red', borderRadius: "4px", padding: '5px' }}>
        {errorMessage}
      </p>
    ))}
  </div>
)}

            <button
              style={{
                width: '100%',
                marginTop: '5vh',
                padding: '1vw',
                backgroundColor: 'Blue',
                color: 'white',
              }}
              onClick={next}
            >
              Next
            </button>

          </div>
        </div>
      </div>
    );
  };

export default Disperse
