import React, { useState, useEffect } from "react";

const Disperse = () => {
  const [inputValue, setInputValue] = useState([]);
  const [error, setError] = useState(null);
  const [showerror, setshowError] = useState(false);
  const [splitdata, setSplitData] = useState([]);
  const [showbutton, setShowbutton] = useState(false);
  const [showtext, setShowText] = useState([]);

  useEffect(() => {
    const tempSplitData = inputValue.map((str, index) => {
      const [address, amount] = str.split(/[ ,=]/);
      return { address, amount: amount, line: index + 1 };
    });

    setShowText(tempSplitData);

    // Check for errors when the input data changes
    const errors = [];
    const duplicateMap = new Map();

    for (let previous = 0; previous < tempSplitData.length; previous++) {
      if (!isNaN(tempSplitData[previous].amount || tempSplitData[previous].amount === "")) {
        // Check for duplicate addresses
        for (let current = previous + 1; current < tempSplitData.length; current++) {
          if (tempSplitData[previous].address === tempSplitData[current].address) {
            if (!duplicateMap.has(tempSplitData[previous].address)) {
              duplicateMap.set(tempSplitData[previous].address, [previous + 1]);
            }
            if (!duplicateMap.get(tempSplitData[previous].address).includes(current + 1)) {
              duplicateMap.get(tempSplitData[previous].address).push(current + 1);
            }
          }
        }
      } else {
        errors.push(`Line ${tempSplitData[previous].line} wrong amount`);
      }
    }

    duplicateMap.forEach((index, address) => {
      errors.push(`Address ${address} has duplicate entries at lines ${index.join(", ")}`);
      setShowbutton(true); // Show the buttons if there are no errors
    });

    if (errors.length > 0) {
      setError(errors);
    } else {
      setError([]);
      setshowError(false);
    }
  }, [inputValue]);

  const onSumbit = () => {
    if (errors.length > 0) {
      setError(errors);
      setshowError(true);
    } else {
      setError([]);
      setshowError(false);
    }
  };

  const handleCombineBalances = (index) => {
    const combinedData = [];
    let counter = 1;

    for (let previous = 0; previous < splitdata.length; previous++) {
      for (let current = previous + 1; current < splitdata.length; current++) {
        if (splitdata[previous].address === splitdata[current].address) {
          const duplicateAmount = parseInt(splitdata[current].amount, 10); // Parse to integer
          splitdata[previous].amount = parseInt(splitdata[previous].amount, 10); // Parse to integer
          splitdata[previous].amount += duplicateAmount;
          // Remove the duplicate entry
          splitdata.splice(current, 1);
          current--; // Adjust the loop index since we removed an element
        }
      }
      // Push non-duplicate items to combinedData
      combinedData.push({
        address: splitdata[previous].address,
        amount: splitdata[previous].amount,
        line: counter++, // Include the line index
      });
    }
    setShowbutton(false);
    setSplitData(combinedData);
  };
  const KeepFirstOne = () => {
    const filteredData = [];
    let counter = 1;
    for (let previous = 0; previous < splitdata.length; previous++) {
      for (let current = previous + 1; current < splitdata.length; current++) {
        if (splitdata[previous].address === splitdata[current].address) {
          // Remove the duplicate entry
          splitdata.splice(current, 1);
          current--; // Adjust the loop index since we removed an element
        }
      }
      // Push non-duplicate items to combinedData
      filteredData.push({
        address: splitdata[previous].address,
        amount: splitdata[previous].amount,
        line: counter++, // Include the line index
      });
    }
    setShowbutton(false);
    setSplitData(filteredData);
  };
  return (
    <div
      style={{
        width: "100%",
        height: "100vh",
        backgroundColor: "whitesmoke",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <div
        style={{
          width: "90%",
          height: "90%",
          backgroundColor: "white",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <div style={{ width: "50%", height: "50%", padding: "3vw" }}>
          <h4>Addresses with Amounts</h4>

          <div
            style={{
              display: "grid",
              gridTemplateColumns: "auto 1fr",
              gap: "10px",
              height: "80%",
              backgroundColor: "#FFCCFF",
            }}
          >
            <span style={{ fontSize: "15px" }}>
              {splitdata.map((item) => (
                <div key={item.line}>{item.line}</div>
              ))}
            </span>
            <textarea
              cols={5}
              style={{
                width: "100%",
                height: "98.5%",
                border: "0px",
                borderLeft: "1px solid grey",
                backgroundColor: "transparent",
              }}
              value={
                splitdata.length > 0
                  ? splitdata
                      .map((item) => `${item.address} ${item.amount}`)
                      .join("\n")
                  : inputValue
              }
              onChange={(e) => {
                setShowText([]);
                setSplitData([]);
                setInputValue([]);
                const text = e.target.value;
                if (text.includes(",")) {
                  // Replace commas with newlines
                  const newText = text.replace(/,/g, "\n");
                  setInputValue(newText.split("\n"));
                } else {
                  setInputValue(text.split("\n"));
                }
              }}
            ></textarea>
          </div>

          <span style={{ color: "#7D7C7C", fontSize: "13px" }}>
            Separated by ','or' 'or'='
          </span>
          {showbutton && (
            <div
              style={{
                display: "flex",
                justifyContent: "flex-end",
                alignItems: "center",
                color: "red",
              }}
            >
              <button
                style={{
                  backgroundColor: "transparent",
                  color: "red",
                  border: "none",
                  marginRight: "5px",
                }}
                onClick={() => {
                  KeepFirstOne();
                  onSumbit();
                }}
              >
                Keep first one
              </button>
              |
              <button
                style={{
                  backgroundColor: "transparent",
                  color: "red",
                  border: "none",
                  marginLeft: "5px",
                }}
                onClick={() => {
                  handleCombineBalances();
                  onSumbit();
                }}
              >
                Combine Balance
              </button>
            </div>
          )}

          {showerror && (
            <div style={{ color: "red", fontSize: "15px", margin: "5px 0" }}>
              {" "}
              {error.some((errorMessage) => errorMessage.startsWith("Address"))
                ? "Duplicated"
                : " "}
              <div
                style={{
                  border: "1px solid red",
                  borderRadius: "4px",
                  padding: "2px",
                  fontSize: "15px",
                }}
              >
                {error.map((errorMessage, index) => (
                  <p
                    key={index}
                    style={{
                      color: "red",
                    }}
                  >
                    {errorMessage}
                  </p>
                ))}
              </div>
            </div>
          )}

          <button
            style={{
              width: "100%",
              marginTop: "3vh",
              padding: "1vw",
              backgroundColor: "Blue",
              color: "white",
              marginLeft: "3px",
            }}
            onClick={() => {
              setSplitData(showtext);
              onSumbit();
            }}
          >
            Next
          </button>
        </div>
      </div>
    </div>
  );
};

export default Disperse;
