import React, { useState } from 'react';

const Disperse = () => {
  const [input, setInput] = useState('');
  const [addresses, setAddresses] = useState([]);
  const [amounts, setAmounts] = useState([]);
  const [errors, setErrors] = useState([]);

  const onSubmit = (e) => {
    e.preventDefault();

    // Reset errors
    setErrors([]);

    // Split the input into address and amount pairs
    const pairs = input.trim().split(', ');
    const newAddresses = [];
    const newAmounts = [];
    const newErrors = [];

    pairs.forEach((pair, index) => {
      const [address, amount] = pair.split('=' || ' ' || ',');

      if (address && !isNaN(amount)) {
        newAddresses.push(address);
        newAmounts.push(amount);
      } else {
        newErrors.push(`Invalid pair on line ${index + 1}: ${pair}`);
      }
    });

    if (newErrors.length > 0) {
      setErrors(newErrors);
    } else {
      setAddresses(newAddresses);
      setAmounts(newAmounts);
    }
  };

  return (
    <div>
      <form onSubmit={onSubmit}>
        <label htmlFor="input">Enter Addresses and Amounts:</label>
        <textarea
          id="input"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="address1=amount1, address2=amount2, ..."
        />
        <button type="submit">Submit</button>
      </form>

      {errors.length > 0 && (
        <div className="error">
          {errors.map((error, index) => (
            <p key={index}>{error}</p>
          ))}
        </div>
      )}

      {addresses.length > 0 && amounts.length > 0 && (
        <div>
          <h2>Addresses:</h2>
          <ul>
            {addresses.map((address, index) => (
              <li key={index}>{address}</li>
            ))}
          </ul>

          <h2>Amounts:</h2>
          <ul>
            {amounts.map((amount, index) => (
              <li key={index}>{amount}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Disperse;
