import React, { useState, useEffect } from 'react';
import axios from 'axios'

const address = [
  "0x2CB99F193549681e06C6770dDD5543812B4FaFE8 10",
  "0xEb0D38c92deB969b689acA94D962A07515CC5204 5",
  "0xF4aDE8368DDd835B70b625CF7E3E1Bc5791D18C1 8",
  "0x09ae5A64465c18718a46b3aD946270BD3E5e6aaB 9",
  "0x8B3392483BA26D65E331dB86D4F430E9B3814E5e 15"
]

const Disperse = () => {
  const [inputValue, setInputValue] = useState(address);
  const [amounts, setAmounts] = useState([]);
  const [error, setError] = useState('');


const onSubmit = () => {
  // Split the input into lines
  const lines = inputValue.split('\n');

  const newAmounts = [];
  const newErrors = [];

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i].trim();
    if (line === '') continue; // Skip empty lines

    const parts = line.split('=');
    if (parts.length !== 2) {
      newErrors.push(`Invalid format on line ${i + 1}`);
      continue;
    }

    const amount = parseFloat(parts[1]);
    if (!isNaN(amount)) {
      newErrors.push(`Amount is not a valid number on line ${i + 1}`);
      continue;
    }

    newAmounts.push(amount);
  }

  // If there are errors, set the error message and clear amounts
  if (newErrors.length > 0) {
    setError(newErrors.join('\n'));
    setAmounts([]);
  } else {
    // If no errors, clear the error message and update amounts
    setError('');
    setAmounts(newAmounts);

    // Perform further actions here with the amounts
  }
};

  return (
    <div
      style={{
        width: '100%',
        height: '100vh',
        backgroundColor: 'whitesmoke',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <div
        style={{
          width: '90%',
          height: '90%',
          backgroundColor: 'white',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        <div style={{ width: '50%', height: '50%', padding: '3vw' }}>
          <h4>Addresses with Amounts</h4>

          <textarea
            cols={5}
            style={{ width: '100%', height: '80%', display: 'block' }}
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
          ></textarea>
          {error && <p style={{ color: 'red' }}>{error}</p>}

          <button
            style={{
              width: '100%',
              marginTop: '5vh',
              padding: '1vw',
              backgroundColor: 'Blue',
              color: 'white',
            }}
            onClick={onSubmit}
          >
            Next
          </button>

        </div>
      </div>
    </div>
  );
};

export default Disperse;
