import React, { useState } from 'react';

const Disperse = () => {
  const [input, setInput] = useState('');
  const [addresses, setAddresses] = useState([]);
  const [amounts, setAmounts] = useState([]);
  const [errors, setErrors] = useState([]);
  const [validLines, setValidLines] = useState([]);
  const [lineHistory, setLineHistory] = useState([]);

  const onSubmit = (e) => {
    e.preventDefault();

    // Reset errors and valid lines
    setErrors([]);
    setValidLines([]);

    // Split the input into address and amount pairs
    const lines = input.split('\n'); // Split by line breaks
    const newAddresses = [];
    const newAmounts = [];
    const newErrors = [];
    const newValidLines = [];

    lines.forEach((line, index) => {
      const [address, amount] = line.split('=');

      if (address && !isNaN(amount)) {
        newAddresses.push(address);
        newAmounts.push(Number(amount));
        newValidLines.push(line);
      } else {
        newErrors.push(`Invalid pair on line ${index + 1}: ${line}`);
      }
    });

    // Check if the current input is the same as any previous line
    if (lineHistory.includes(input)) {
      newErrors.push('Duplicate line: ' + input);
    }

    // Check if the current addresses are the same as the previous addresses
    if (JSON.stringify(newAddresses) === JSON.stringify(addresses)) {
      newErrors.push('Addresses are the same as the previous value.');
    }

    if (newErrors.length > 0) {
      setErrors(newErrors);
    } else {
      setErrors([]);

      const mergedAddresses = [...addresses];
      const mergedAmounts = [...amounts];

      // Merge duplicate lines by adding amounts to the previous line
      for (let i = 0; i < newAddresses.length; i++) {
        const address = newAddresses[i];
        const amount = newAmounts[i];

        if (mergedAddresses.includes(address)) {
          const index = mergedAddresses.indexOf(address);
          mergedAmounts[index] += amount;
        } else {
          mergedAddresses.push(address);
          mergedAmounts.push(amount);
        }
      }

      setAddresses(mergedAddresses);
      setAmounts(mergedAmounts);
      setValidLines(newValidLines);

      // Append each line to the line history
      setLineHistory((prevHistory) => [...prevHistory, ...lines]);

      // Clear the input after submission
      setInput('');
    }
  };

  const handleAddClick = (line) => {
    // Find the index of the current line
    const lineIndex = validLines.indexOf(line);
    if (lineIndex !== -1) {
      // Extract the address and amount from the current line
      const [address, amountStr] = line.split('=');
      const amountToAdd = parseFloat(amountStr);

      // Update the amount in the corresponding address
      const addressIndex = addresses.indexOf(address);
      if (addressIndex !== -1) {
        const updatedAmount = amounts[addressIndex] + amountToAdd;

        // Remove the current line and update the amounts
        setValidLines((prevValidLines) =>
          prevValidLines.filter((validLine, index) => index !== lineIndex)
        );
        setAddresses((prevAddresses) => [
          ...prevAddresses.slice(0, addressIndex),
          ...prevAddresses.slice(addressIndex + 1),
        ]);
        setAmounts((prevAmounts) => [
          ...prevAmounts.slice(0, addressIndex),
          updatedAmount,
          ...prevAmounts.slice(addressIndex + 1),
        ]);
      }
    }
  };

  return (
    <div>
      <form onSubmit={onSubmit}>
        <label htmlFor="input">Enter Addresses and Amounts:</label>
        <textarea
          id="input"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="address1=amount1, address2=amount2, ..."
          rows="4" // You can adjust the number of visible rows as needed
        />
        <button type="submit">Submit</button>
      </form>

      {errors.length > 0 && (
        <div className="error">
          {errors.map((error, index) => (
            <p key={index}>{error}</p>
          ))}
        </div>
      )}

      {lineHistory.length > 0 && (
        <div>
          <h2>Line History:</h2>
          <ul>
            {lineHistory.map((line, index) => (
              <li key={index}>
                {line}{' '}
                {(errors.includes('Duplicate line: ' + line) ||
                  errors.includes('Addresses are the same as the previous value.')) && (
                  <button onClick={() => handleAddClick(line)}>Add</button>
                )}
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Display other sections as needed */}
    </div>
  );
};

export default Disperse;
