import React, { useState, useEffect } from 'react';
// import axios from 'axios'

const address = [
  "0x2CB99F193549681e06C6770dDD5543812B4FaFE8 10",
  "0xEb0D38c92deB969b689acA94D962A07515CC5204 5",
  "0xF4aDE8368DDd835B70b625CF7E3E1Bc5791D18C1 8d",
  "0x09ae5A64465c18718a46b3aD946270BD3E5e6aaB 9",
  "0x8B3392483BA26D65E331dB86D4F430E9B3814E5e 15",
  "0x8B3392483BA26D65E331dB86D4F430E9B3814E5e 15"
]

const Disperse = () => {
  const [inputValue, setInputValue] = useState(address);
  const [error, setError] = useState(null); // Define the error state
const [splitdata, setSplitData] = useState([])
  const [showbutton, setShowbutton] = useState(true)
 useEffect(() => {
  const tempSplitData = inputValue.map((str) => {
    const [address, amount] = str.split(' ');
    return { address, amount: amount };
  });

  setSplitData(tempSplitData);
}, [inputValue]);


    console.log(splitdata)


  // const splitLine = (line) => {
  //   const parts = line.split('=');
  //   if (parts.length !== 2) {
  //     return null; // Invalid format
  //   }

  //   const address = parts[0].trim();
  //   const amount = parseFloat(parts[1]);

  //   if (isNaN(amount)) {
  //     return null; // Amount is not a valid number
  //   }

  //   console.log(`${address} + ${amount}`); // Corrected string interpolation
  //   return { address, amount };
  // };



  const next = () => {
    const errors = [];
    const seenAddresses = new Set();


    splitdata.forEach((item, index) => {
      if (isNaN(item.amount)) {
        errors.push(`Line ${index + 1} wrong amount`);
      }
      if (seenAddresses.has(item.address)) {
        errors.push(`Line ${index + 1} has a duplicate address`);
        setShowbutton(true)
        console.log(showbutton)
      } else {
        seenAddresses.add(item.address);
      }
    });

    if (errors.length > 0) {
      // Set the error state to display errors in the component
      setError(errors);
    } else {
      // Clear the error state if there are no errors
      setError([]);
      // Proceed with the valid data
      // Your logic here for handling valid data
    }
  };




    return (
      <div
        style={{
          width: '100%',
          height: '100vh',
          backgroundColor: 'whitesmoke',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        <div
          style={{
            width: '90%',
            height: '90%',
            backgroundColor: 'white',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <div style={{ width: '50%', height: '50%', padding: '3vw' }}>
            <h4>Addresses with Amounts</h4>

            <textarea
              cols={5}
              style={{ width: '100%', height: '80%', display: 'block' }}
              value={inputValue}

            ></textarea>
           {error && (
  <div>
    {error.map((errorMessage, index) => (
      <p key={index} style={{ color: 'red', border: '1px solid red', borderRadius: "4px", padding: '5px' }}>
        {errorMessage}
      </p>
    ))}
  </div>
)}

            <button
              style={{
                width: '100%',
                marginTop: '5vh',
                padding: '1vw',
                backgroundColor: 'Blue',
                color: 'white',
              }}
              onClick={next}
            >
              Next
            </button>

          </div>
        </div>
      </div>
    );
  };

export default Disperse
